<?phpif(isset($_POST['nom'])){	$sql="SELECT num FROM `skin` WHERE  num= (select max(num)  from `skin` WHERE classe=".$_POST['classe'].")";	$sql .= "ORDER BY num DESC LIMIT 1";	$num = loadSqlResult($sql);		$last_num= $num +1;		if($last_num< 3)	$last_num =3;	$sql="INSERT INTO `skin` (name,price,num,classe,gender,event_id) VALUES ('".$_POST['nom']."',".$_POST['prix'].",".$last_num.",".$_POST['classe'].",".$_POST['genre'].",0)";	loadSqlExecute($sql);		$chemin = "pictures/classe/".$_POST['classe']."/ico/".$last_num."-";		move_uploaded_file($_FILES['image1']['tmp_name'], $chemin."1.gif");	move_uploaded_file($_FILES['image2']['tmp_name'], $chemin."2.gif");	move_uploaded_file($_FILES['image3']['tmp_name'], $chemin."3.gif");	move_uploaded_file($_FILES['image4']['tmp_name'], $chemin."4.gif");			if($_POST['skin_newsletter'] == 1)	{		$title = "Nouveau skin ".$_POST['nom'];				$title .= " (".getClasseNameById($_POST['classe'])." - ".getGenderById($_POST['genre']).") ";				$body = "Ce nouveau skin est au prix de : ".$_POST['prix']." points <br />";				$body .= "<div style=\"margin-left:70px;\"".				 "<img src=\"".$chemin."1.gif\" />".				 "<img src=\"".$chemin."2.gif\" />".				 "<img src=\"".$chemin."3.gif\" />".				 "<img src=\"".$chemin."4.gif\" />".				 "</div>";									$sql = "INSERT INTO `news` (date,title,content) VALUES (NOW(),'$title','$body')";		loadSqlExecute($sql);		//pre_dump(htmlentities($sql));			}		}if(isset($_POST['skin'])){	$sql='SELECT id FROM users WHERE login="'.$_POST['compte'].'" ';	$user_id=loadSqlResult($sql);		$sql='SELECT id FROM `skin` WHERE name = "'.$_POST['skin'].'" ';	$skin_id = loadSqlResult($sql);		$sql='INSERT skin_on_user (user_id,skin_id) VALUES('.$user_id.','.$skin_id.')';	loadSqlExecute($sql);		echo '<h3 style="color:red"> Skin ajouté au joueur</h3>';	}?><fieldset>	<legend>Enregistrer un skin</legend>	<form method="post" enctype="multipart/form-data" action="panneauAdmin.p