<div class="marginTop30">	<div class="paddingLeft15 fontW700"> Liste des screenshots, cliquez sur un screenshot pour l\'aggrandir </div>	<hr />            <div class="nodiplay"><input id="current_page" type="text" value="1" /></div>';            <div class="heigth85"><!--		// flèche pour aller vers la gauche-->		<div class="left width10P center">			<img src="pictures/utils/grande_fleche_g.png" onclick="switchScreens(-1);" alt="<<" class="marginTop10"/>		</div>		<div id="screen_mini_container" class="left width70P center"><!--		// On récupère les screenshots--><?php			$dirname = 'pictures/screenshots';			$dir = opendir($dirname); 			$limit = 1;			while($file = readdir($dir))                         {				$id = str_replace(".jpg", "",$file);				if($file != '.' && $file != '..' && !is_dir($dirname.$file))				{					$arrayPictures[$id] = $file;				}			}			closedir($dir);			$i = 0;			$j = 1;			echo '<div id="1">';			foreach($arrayPictures as $id=>$picture)			{				if($i == 0 && $j != 1)				{					echo '<div id="'.$j.'" class="nodisplay">';					$j++;				}				echo '<div class="left marginLeft12 center">';					$url = 'page/view_screen.php?screen='.$picture;					$target = 'screen_container';					$onclick= "HTTPTargetCall('$url','$target');";					echo '<img style="width:110px;height:77px;border:solid 1px white;cursor:pointer;" src="pictures/screenshots/'.$picture.'" alt="'.$id.'" onclick="'.$onclick.'" />';				echo '</div>';				$i++;				if($i == 5)				{					echo '</div>';					$i = 0;				}			}			// Cas ou un div est ouvert m'est n'a pas été fermé			if($i != 0) 				echo '</div>';?>							</div><!--		// flèche pour aller vers la droite-->		<div class="left width10P center">			<img src="pictures/utils/grande_fleche_d.png" onclick="switchScreens(1);" alt=">>" class="marginTop10"/>		</div>	</div>	<hr />		<div class="nodisplay"><input id="max_page" type="text" value="'.$j.'" /></div>	<div id="screen_container">    </div></div>