<?phprequire_once($server.'/class/guild.class.php');$char = unserialize($_SESSION['char']); $guild = new guild($char->getGuildId()); if(empty($_GET['do'])) $_GET['do'] = ''; switch($_GET['do']) { 	case 'accept': 		$guild->accepteCandidature(new char($_GET['id'])); 	break; 	case 'refuse': 		$guild->deleteCandidature(new char($_GET['id'])); 	break; } echo '<div class="backgroundBodyNoRadius" style="margin:auto;width:550px;margin-top:15px;">';	echo '<div class="backgroundMenuNoRadius" style="padding-left:20px;">';		echo 'Candidatures';	echo '</div>';	echo '<div style="padding:5px;height:450px;margin-bottom:10px;margin-top:10px;">';		$candidatures = $guild->getCandidatures();		if(count($candidatures)>= 1)		{			foreach($candidatures as $candidature)			{				$author = char::getNameById($candidature['char_id']);				$hours = timestampToHour($candidature['timestamp'],'h');				$date = timestampToDate($candidature['timestamp'],'/');								echo '<div class="backgroundMenuNoRadius" style="z-index:1;font-weight:700;padding-left:15px;border:solid 1px black;cursor:pointer;margin-top:10px;" onclick="show(\'message_text_'.$candidature['id'].'\');" >';						echo ' De '.$author.', Le '.$date.' &agrave; '.$hours;					if($char->isMeneur() or $char->isLord())					{						// Bouton refuser						$url = "page.php?category=guilde&mode=candidatures&do=refuse&id=".$candidature['char_id'];						$onclick = "if(confirm('Refuser cette candidature ?')){HTTPTargetCall('$url','bodygameig');}";						echo '<div style="float:right;margin-right:4px;z-index:3;">';							echo '<img style="z-index:36;" src="pictures/utils/no.gif" onclick="'.$onclick.'" alt="X" title="Refuser" />';						echo '</div>';						// Bouton accepter						$url = "page.php?category=guilde&mode=candidatures&do=accept&id=".$candidature['char_id'];						$onclick = "if(confirm('Accepter cette candidature ?')){HTTPTargetCall('$url','bodygameig');}";						echo '<div style="float:right;margin-right:5px;z-index:3;">';							echo '<img style="z-index:36;" src="pictures/utils/yes.gif" onclick="'.$onclick.'" alt="X" title="Accepter" />';						echo '</div>';					}				echo '</div>';				echo '<div id="message_text_'.$candidature['id'].'" style="padding-left:30px;padding-right:30px;padding-top:5px;display:none;">';					echo $candidature['message'];				echo '</div>';			}		}else{			echo '<div style="margin-top:20px;padding-left:15px;font-weight:700;">';				echo 'Pas de candidatures';			echo '</div>';		}	echo '</div>';echo '</div>';?>