<?phprequire_once($server.'/class/message.class.php');if(empty($_GET['do'])) $_GET['do'] = '';if(empty($_GET['to_prevalue'])) $_GET['to_prevalue'] = '';if(empty($_GET['title'])) $_GET['title'] = '';if(empty($_POST['message'])) $_POST['message'] = '';if($_GET['do'] == 'add'){    echo '<div style="margin-left:25px;margin-top:20px;">';    if(char::exist($_POST['to']))    {        if($_POST['title'] != "")        {            if($_POST['message'] != "")            {                $to = char::getIdByName($_POST['to']);                message::addNewMessage($char->getId(),$to,$_POST['title'],$_POST['message']);                printConfirm('<b>Le message a bien &eacute;t&eacute; envoy&eacute;</b>');            }else{                printAlert('Attention le message est vide ! ');                $error = 1;            }        }else{            printAlert('Attention le titre ne peux pas &ecirc;tre vide');            $error = 1;        }    }else{        printAlert('Le destinataire n\'existe pas. Tu as du surement faire une faute de frappe.');        $error = 1;        }	    echo '</div>';}if(!empty($_GET['to']))	$_GET['to_prevalue'] = char::getNameById($_GET['to']);if($_GET['do'] != 'add' or $error == 1){	echo '<form id="form_new_message" method="POST">';		echo '<div style="margin-left:30px;margin-top:20px;color:white;">';			$array = getAutocomplete('chars',array($char->getId()));			echo ' Destinataire : <input id="to_input" type="text" value="'.$_GET['to_prevalue'].'" name="to" size="37" onfocus="autoComplete(\'to_input\',\''.$array.'\')" /> <br /> <br /> ';			echo ' Titre : <input type="text" name="title" size="45" maxlength="50" value="'.$_GET['title'].'" />';		echo '</div>';		echo '<div style="margin-left:30px;margin-top:20px;color:white;">';			echo ' &nbsp; &nbsp; &nbsp; Message : <br />';			echo '<textarea name="message" style="width:500px;height:100px;">';				echo $_POST['message'];			echo '</textarea>';		echo '</div>';		echo '<div style="width:500px;text-align:right;margin-top:5px;color:white;">';			$url = 'page.php?category=messagerie&action=new&do=add';			$target = 'box_container';			$onclick = "HTTPPostCall('$url','form_new_message','$target');";			echo '<input class="button" type="button" value="Envoyer" onclick="'.$onclick.'" />';		echo '</div>';	echo '</form>';	}?>