<?php// Chargement des données statiques$typage = array('1'=>'R&eacute;colte','2'=>'Artisanat','3'=>'Magique');// Affichageecho '<div style="float:left;border-right:solid 2px black;min-height:600px;">';	$metier_list = metier::getInfoMetierForChar($char->getId());	$type = 0;	foreach($metier_list as $metier)	{		// Affichage du container + du menu		if($type != $metier['type'])		{			if($type != 0) echo '</div>';			$type = $metier['type'];			echo '<div id="type_metier_'.$type.'">';			echo '<div style="font-weight:700;margin-top:5px;font-size:18px;">'.$typage[$type].'</div>';		}		// Affichage du métier		echo '<div style="margin-left:15px;margin-top:5px;">';			$url = 'pageig/profil/info_metier.php?id='.$metier['id'];			$onclick="HTTPTargetCall('".$url."','metier_details_container');";			echo '<div style="font-size:15px;font-weight:600;cursor:pointer;" onclick="'.$onclick.'"> - ';				echo $metier['name'];				echo '</div>';			echo '<div style="font-size:12px;margin-left:20px;">';				echo 'niveau '.$metier['level'];			echo '</div>';			echo '<div>';				$pourcent = char::calculPourcentExp($metier['exp'],$metier['level']);				showBarre('exp',$pourcent,0,0,false);			echo '</div>';		echo '</div>';	}	echo '</div>';echo '</div>';echo '<div id="metier_details_container" style="float:left;border:solid 1 px;width:610px;">';	echo '<div style="margin-left:70px;margin-top:60px;">' .			'Cliquer sur un m&eacute;tier pour obtenir le d&eacute;tail' .			'</div>';echo '</div>';?>